SELECT a.*
  FROM service_agreement@fokus a, subscriber@fokus s, campaign_commitments@fokus c
 WHERE RTRIM(a.soc)      IN ('PPUT'/*,      'PSSL'    */)
   AND RTRIM(a.campaign) IN ('PPUT12R25'/*, 'PSSL24BI'*/)
   AND SYSDATE     BETWEEN a.effective_date AND NVL(a.expiration_date, SYSDATE + 1)
   AND a.ban             = s.customer_id
   AND a.subscriber_no   = s.subscriber_no
   AND s.sub_status      = 'A'
   AND RTRIM(a.campaign) = RTRIM(c.campaign)
   AND a.effective_date + (c.min_commit_period * 30) > SYSDATE -- The campaign has not yet expired.   
;

